<script type="text/javascript">
$(document).ready(function(){
		$('#chanle2').click(function() {
		$('#chanle2').html('Đang Xử Lý...').prop('disabled', true);
		var formData = {
            'noidung'       : $("#noidung").val(),
            'money'         : $("#money").val()
	    };
		$.post("/api/chanle2", formData,
			function (data) {
			    if(data.status == 'error'){
				Swal.fire('Thông Báo', data.msg, data.status);
				$('#chanle2').html('Thanh Toán').prop('disabled', false);
			    } else {
			     setTimeout(function(){ location.href = "" },2000); 
			    Swal.fire('Thông Báo', data.msg, data.status);
			     $('#chanle2').html('Thanh Toán').prop('disabled', false);
			    }
		}, "json");
	});
});
</script>
